import axios from "axios";
import React,{useState,useEffect} from "react";
import ProductRow from "./ProductRow";

const PRODUCTS = [
  {category: 'Herramienta', unity: '2', stocked: true, name: 'Martillo'},
  {category: 'Acueducto', unity: '20', stocked: true, name: 'Tubo'},
  {category: 'Acueducto', unity: '0', stocked: false, name: 'Medidor'},
  {category: 'Acueducto', unity: '10', stocked: true, name: 'Tapa'},
  {category: 'Acueducto', unity: '0', stocked: false, name: 'Cajilla'},
  {category: 'Accesorio Acueducto', unity: '200', stocked: true, name: 'Codo'}
];
export default function CallAPIView() {
  const [rows,setRows]: any[] =useState([])

  useEffect(() => {
    fetchRows();
  }, [])
  useEffect(() => {
    console.log(rows)
  }, [rows])
  const fetchRows=async()=>{
    const response=await axios('http://localhost:3001/getProducts');
    setRows(response.data)    
  }

    /*PRODUCTS.forEach((product:any) => {
      console.log(product);
      rows.push(
        <ProductRow
        product={product} />
      );
  });*/

    return (
      <div className="App">
      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Categoria</th>
            <th>Unidades</th>
          </tr>
        </thead>
        <tbody>{rows}</tbody>
      </table>
      </div>
    )
  };
