import "./App.css";
import SuperTokens, { SuperTokensWrapper, getSuperTokensRoutesForReactRouterDom } from "supertokens-auth-react";
import Session, { SessionAuth } from "supertokens-auth-react/recipe/session";
import { Routes, BrowserRouter as Router, Route } from "react-router-dom";
import Home from "./Home";
import { SuperTokensConfig } from "./config";
import { SignInAndUp } from 'supertokens-auth-react/recipe/emailpassword';
import Auth from "./Auth";

SuperTokens.init({  appInfo: {
    appName: "El Punto Del Agua",
    apiDomain: "http://localhost:3001",
    websiteDomain: "http://localhost:3000",
},
// recipeList contains all the modules that you want to
// use from SuperTokens. See the full list here: https://supertokens.com/docs/guides
recipeList: [ 
    EmailPassword.init({
    signInAndUpFeature: {
        disableDefaultUI: true,
        signUpForm: {
            formFields: [{
                id: "email",
                label: "Email",
                placeholder: "Ingrese un email valido"
            },
            {
                id: "password",
                label: "contraseña",
                placeholder: "Ingrese su contraseña"
            },
            {
                id: "age",
                label: "Tu edad",
                placeholder: "Que edad tienes?",
                optional: true,

                /* Validation method to make sure that age is above 18 */
                validate: async (value) => {
                    if (parseInt(value) > 18) {
                        return undefined; // means that there is no error
                    }
                    return "Menor de edad";
                }

            }, 
            {
                id: "area",
                label: "Area",
                placeholder: "Area en la que labora",
                optional: true
            }
        ],
        termsOfServiceLink: "https://example.com/terms-of-service",
        privacyPolicyLink: "https://example.com/privacy-policy",
        },
        signInForm: {
            formFields: [{
                id: "email",
                label: "Email",
                placeholder: "Ingrese un email valido"
            },
            {
                id: "password",
                label: "contraseña",
                placeholder: "Ingrese su contraseña"
            }
        ]
        }
    }
}), Session.init()], getRedirectionURL: async (context) => {
    if (context.action === "TO_AUTH") {
        return "/auth";
    };
}});

function App() {
    let sessionContext = Session.useSessionContext();
    if (sessionContext.loading) {
        return null;
    }
    if (sessionContext.doesSessionExist) {
        // We wrap this with <SessionAuth /> so that
        // all claims are validated before showing the logged in UI.
        // For example, if email verification is switched on, and
        // the user's email is not verified, then <SessionAuth />
        // will redirect to the email verification page.
        return (      
        <SessionAuth>
            <Home />
        </SessionAuth>
        )
    } else {
        return (
            <div>
                <Auth />
            </div>
        )
    }
}

export default App;
